package com.tel_labs.ex2;

import java.util.List;
import java.util.Map;

import com.tel_labs.ex2.entity.Plan;
import com.tel_labs.ex2.response.PriceOutput;
import com.tel_labs.ex2.service.PlanService;
import com.tel_labs.ex2.utils.DataGenerator;

public class App {
    public static void main(String[] args) {
        try {
            DataGenerator generator = new DataGenerator();
            Map<String, Plan> planFeatureMap = generator.generatePlanFeatureMap(args[0]);
            List<String> requiredFeatures = generator.generateRequiredFeatureList(args[1]);

            PlanService service = new PlanService();
            PlanService.setPlanFeatureMap(planFeatureMap);
            PriceOutput output = service.generateOutput(requiredFeatures);

            String outputStr = Integer.toString(output.getPrice());
            if (output.getPlans().length > 0) {
                for (String plan : output.getPlans()) {
                    outputStr += "," + plan;
                }
            }
            System.out.println(outputStr);
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}
